mapper.select:
SELECT COUNT(*)
FROM RC_RECIPE
WHERE USER_NO = #{userNo}
  AND STATUS_CODE = #{statusCode}

SELECT EXISTS(
    SELECT 1
    FROM RC_RECIPE
    WHERE RECIPE_NO = #{recipeNo}
)

SELECT EXISTS(
    SELECT 1
    FROM RC_LIKE
    WHERE USER_NO = #{userNo}
      AND RECIPE_NO = #{recipeNo}
)

mapper.update:
UPDATE RC_LIKE
SET LIKE_YN = #{likeYn},
    EDIT_DATETIME = NOW(),
    EDIT_USER = #{userNo}
WHERE USER_NO = #{userNo}
  AND RECIPE_NO = #{recipeNo}
